import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios';
import './PatternLearningFeedback.css';

function highlightMatch(context, match) {
  if (!match) return context;
  const idx = context.toLowerCase().indexOf(match.toLowerCase());
  if (idx === -1) return context;
  return (
    <>
      {context.slice(0, idx)}
      <span style={{ background: '#ffe066', fontWeight: 'bold' }}>{context.slice(idx, idx + match.length)}</span>
      {context.slice(idx + match.length)}
    </>
  );
}

function RegexTestModal({ open, onClose, context, defaultPattern, serverUrl }) {
  const [pattern, setPattern] = useState(defaultPattern || '');
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleTest = async () => {
    setLoading(true);
    setResult(null);
    try {
      const controller = new AbortController();
      const timeoutId = setTimeout(() => controller.abort(), 10000); // 10 second timeout

      const resp = await fetch(`${serverUrl}/pattern-learning/test-regex`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ pattern, text: context, context_chars: 50 }),
        signal: controller.signal
      });

      clearTimeout(timeoutId);
      const data = await resp.json();
      setResult(data);
    } catch (e) {
      if (e.name === 'AbortError') {
        setResult({ matches: [], contexts: ['Error: Request timed out after 10 seconds'] });
      } else {
        setResult({ matches: [], contexts: ['Error: ' + e.message] });
      }
    }
    setLoading(false);
  };

  if (!open) return null;
  return (
    <div style={{ position: 'fixed', top: 0, left: 0, width: '100vw', height: '100vh', background: '#0008', zIndex: 1000, display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
      <div style={{ background: '#fff', padding: 32, borderRadius: 12, minWidth: 400, maxWidth: 600 }}>
        <h3>Live Regex Tester</h3>
        <div style={{ marginBottom: 12 }}>
          <label>Regex Pattern:</label>
          <input value={pattern} onChange={e => setPattern(e.target.value)} style={{ width: '100%', fontFamily: 'monospace', fontSize: 16, marginTop: 4 }} />
        </div>
        <button onClick={handleTest} style={{ marginBottom: 16, background: '#6c47ff', color: '#fff', border: 'none', borderRadius: 6, padding: '8px 20px', fontWeight: 'bold', cursor: 'pointer' }} disabled={loading}>
          {loading ? 'Testing...' : 'Test Regex'}
        </button>
        {result && (
          <div>
            <div style={{ marginBottom: 8 }}><b>Matches:</b> {result.matches && result.matches.length > 0 ? result.matches.join(', ') : 'None'}</div>
            <div style={{ marginBottom: 8 }}><b>Context:</b></div>
            {result.contexts && result.contexts.map((ctx, i) => (
              <div key={i} style={{ background: '#f6f6f6', padding: 8, marginBottom: 4, borderRadius: 4 }}>
                {result.matches && result.matches[i] ? highlightMatch(ctx, result.matches[i]) : ctx}
              </div>
            ))}
          </div>
        )}
        <button onClick={onClose} style={{ marginTop: 16, background: '#eee', color: '#333', border: 'none', borderRadius: 6, padding: '8px 20px', fontWeight: 'bold', cursor: 'pointer' }}>Close</button>
      </div>
    </div>
  );
}

const PatternLearningFeedback = ({ serverUrl, caseId = "54820" }) => {
  const [feedback, setFeedback] = useState({ extractionId: '', isCorrect: true, comments: '' });
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');
  const [enhancedAnalysis, setEnhancedAnalysis] = useState(null);
  const [regexModal, setRegexModal] = useState({ open: false, context: '', pattern: '' });
  const [error, setError] = useState(null);
  const [testResult, setTestResult] = useState(null);
  const [logs, setLogs] = useState([]);
  const [showLogs, setShowLogs] = useState(false);
  const [logPolling, setLogPolling] = useState(false);
  const pollingRef = useRef();
  const [feedbackStatus, setFeedbackStatus] = useState({}); // {extraction_id: 'trained'|'error'}
  const [suggestModal, setSuggestModal] = useState({ open: false, extractionId: null });
  const [suggestValue, setSuggestValue] = useState('');

  const [activeTab, setActiveTab] = useState('endpoint-tester');
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  const [analysisResults, setAnalysisResults] = useState(null);
  const [extractions, setExtractions] = useState([]);
  const [formDetections, setFormDetections] = useState([]);
  const [feedbackSubmitted, setFeedbackSubmitted] = useState(new Set());
  const [suggestions, setSuggestions] = useState({});
  const [showRegexTester, setShowRegexTester] = useState(false);
  const [regexTestPattern, setRegexTestPattern] = useState('');
  const [regexTestText, setRegexTestText] = useState('');
  const [regexTestResults, setRegexTestResults] = useState(null);

  // New state for pattern learning dashboard
  const [patternStats, setPatternStats] = useState(null);
  const [flaggedPatterns, setFlaggedPatterns] = useState([]);
  const [patternSuggestions, setPatternSuggestions] = useState([]);
  const [learningStats, setLearningStats] = useState(null);

  const API_BASE = 'http://localhost:8000';

  const fetchEnhancedAnalysis = async () => {
    setLoading(true);
    setError(null);
    try {
      const resp = await fetch(`${serverUrl}/enhanced-analysis/wi/${caseId}`);
      const data = await resp.json();
      if (data.success) {
        setEnhancedAnalysis(data.data);
      } else {
        setError(data.message || 'Failed to fetch analysis');
      }
    } catch (e) {
      setError(e.message);
    }
    setLoading(false);
  };

  const handleFeedback = async (extractionId, isCorrect) => {
    if (feedbackStatus[extractionId] === 'trained') return;
    if (!isCorrect) {
      setSuggestModal({ open: true, extractionId });
      return;
    }
    try {
      const resp = await fetch(`${serverUrl}/pattern-learning/feedback`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          extraction_id: extractionId,
          is_correct: isCorrect,
          correct_value: null,
          comments: null
        })
      });
      if (resp.ok) {
        setFeedbackStatus((prev) => ({ ...prev, [extractionId]: 'trained' }));
        setMessage('Feedback recorded!');
      } else {
        setFeedbackStatus((prev) => ({ ...prev, [extractionId]: 'error' }));
        setMessage('Error recording feedback');
      }
    } catch (e) {
      setFeedbackStatus((prev) => ({ ...prev, [extractionId]: 'error' }));
      setMessage('Error recording feedback: ' + e.message);
    }
  };

  const handleSuggestSubmit = async () => {
    const extractionId = suggestModal.extractionId;
    try {
      const resp = await fetch(`${serverUrl}/pattern-learning/feedback`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          extraction_id: extractionId,
          is_correct: false,
          correct_value: suggestValue,
          comments: 'User suggested value/pattern'
        })
      });
      if (resp.ok) {
        setFeedbackStatus((prev) => ({ ...prev, [extractionId]: 'trained' }));
        setMessage('Suggestion recorded!');
      } else {
        setFeedbackStatus((prev) => ({ ...prev, [extractionId]: 'error' }));
        setMessage('Error recording suggestion');
      }
    } catch (e) {
      setFeedbackStatus((prev) => ({ ...prev, [extractionId]: 'error' }));
      setMessage('Error recording suggestion: ' + e.message);
    }
    setSuggestModal({ open: false, extractionId: null });
    setSuggestValue('');
  };

  const handleFormTypeFeedback = async (extractionId, correctFormType) => {
    try {
      const resp = await fetch(`${serverUrl}/pattern-learning/feedback`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          extraction_id: extractionId,
          is_correct: false,
          correct_value: correctFormType,
          comments: `Form type should be ${correctFormType}`
        })
      });

      if (resp.ok) {
        setMessage(`Form type feedback recorded: should be ${correctFormType}`);
      } else {
        setMessage('Error recording form type feedback');
      }
    } catch (e) {
      setMessage('Error recording form type feedback: ' + e.message);
    }
  };

  const handleFormDetectionFeedback = async (formDetectionId, isCorrect, correctFormType = null) => {
    try {
      const resp = await fetch(`${serverUrl}/pattern-learning/feedback`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          extraction_id: formDetectionId,
          is_correct: isCorrect,
          correct_value: correctFormType,
          comments: correctFormType ? `Form detection should be ${correctFormType}` : 'Form detection feedback'
        })
      });

      if (resp.ok) {
        setMessage(`Form detection feedback recorded: ${isCorrect ? 'Correct' : 'Incorrect'}${correctFormType ? ` (should be ${correctFormType})` : ''}`);
      } else {
        setMessage('Error recording form detection feedback');
      }
    } catch (e) {
      setMessage('Error recording form detection feedback: ' + e.message);
    }
  };

  const groupExtractionsByForm = (extractions) => {
    const grouped = {};
    extractions.forEach(ex => {
      const formDetectionId = ex.form_detection_id || 'unknown';
      if (!grouped[formDetectionId]) {
        grouped[formDetectionId] = {
          formDetection: {
            form_detection_id: formDetectionId,
            form_type: ex.form_type,
            form_confidence: ex.form_confidence,
            form_pattern: ex.form_pattern
          },
          extractions: []
        };
      }
      grouped[formDetectionId].extractions.push(ex);
    });
    return grouped;
  };

  const getConfidenceColor = (confidence) => {
    if (confidence >= 0.8) return '#4caf50'; // Green
    if (confidence >= 0.6) return '#ff9800'; // Orange
    return '#f44336'; // Red
  };

  const getConfidenceBackground = (confidence) => {
    if (confidence < 0.7) return '#fff3cd'; // Light yellow warning
    return 'transparent';
  };

  const getWarningIcon = (extraction) => {
    const warnings = [];

    // Low confidence warning
    if (extraction.field_confidence < 0.7) {
      warnings.push('âš ï¸ Low confidence');
    }

    // False positive warning
    if (extraction.pattern_stats?.false_positive_rate > 0.3) {
      warnings.push('ðŸš¨ High false positive rate');
    }

    // Zero value warning
    if (extraction.extracted_value === '$0.00' || extraction.extracted_value === '0.00') {
      warnings.push('ðŸ’° Zero value detected');
    }

    return warnings;
  };

  // Fetch logs from backend
  const fetchLogs = async () => {
    try {
      const resp = await fetch(`${serverUrl}/enhanced-analysis/logs?limit=200`);
      if (resp.ok) {
        const data = await resp.json();
        setLogs(data.logs || []);
      }
    } catch (e) {
      console.error('Error fetching logs:', e);
    }
  };

  // Clear logs
  const clearLogs = async () => {
    try {
      const resp = await fetch(`${serverUrl}/enhanced-analysis/logs`, {
        method: 'DELETE'
      });
      if (resp.ok) {
        setLogs([]);
        setMessage('Logs cleared');
      }
    } catch (e) {
      setMessage('Error clearing logs: ' + e.message);
    }
  };

  // Start log polling
  const startLogPolling = () => {
    setLogPolling(true);
  };

  // Stop log polling
  const stopLogPolling = () => {
    setLogPolling(false);
  };

  // Poll logs when logPolling is true
  useEffect(() => {
    if (!logPolling) return;
    pollingRef.current = setInterval(() => {
      fetchLogs();
    }, 1000);
    return () => clearInterval(pollingRef.current);
  }, [logPolling]);

  // Get log level color
  const getLogLevelColor = (level) => {
    switch (level) {
      case 'ERROR': return '#dc3545';
      case 'WARNING': return '#ffc107';
      case 'INFO': return '#17a2b8';
      default: return '#6c757d';
    }
  };

  // Format timestamp
  const formatTimestamp = (timestamp) => {
    try {
      const date = new Date(timestamp);
      return date.toLocaleTimeString();
    } catch {
      return timestamp;
    }
  };

  const runEnhancedAnalysis = async () => {
    if (!caseId.trim() || !documentUrls.trim()) {
      setMessage('Please enter both Case ID and Document URLs');
      return;
    }

    setEnhancedAnalysis(null);
    setMessage('Starting enhanced analysis...');
    setShowLogs(true);
    startLogPolling();

    try {
      const urls = documentUrls.split('\n').filter(url => url.trim());
      const requestBody = {
        case_id: caseId.trim(),
        document_urls: urls,
        quick_mode: quickMode
      };

      const resp = await fetch(`${serverUrl}/enhanced-analysis/enhanced-wi-analysis`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(requestBody)
      });

      if (resp.ok) {
        const result = await resp.json();
        setEnhancedAnalysis(result);
        setMessage(`âœ… Enhanced analysis completed in ${result.total_time?.toFixed(2) || 0}s`);
      } else {
        const error = await resp.text();
        setMessage(`âŒ Analysis failed: ${error}`);
      }
    } catch (e) {
      setMessage(`âŒ Analysis error: ${e.message}`);
    } finally {
      stopLogPolling();
      setTimeout(fetchLogs, 1000);
    }
  };

  // New functions for pattern learning dashboard
  const fetchLearningStats = async () => {
    try {
      const response = await fetch(`${API_BASE}/pattern-learning/learning-stats`);
      const data = await response.json();
      setLearningStats(data);
    } catch (error) {
      console.error('Error fetching learning stats:', error);
    }
  };

  const fetchPatternPerformance = async () => {
    try {
      const response = await fetch(`${API_BASE}/pattern-learning/pattern-performance`);
      const data = await response.json();
      setPatternStats(data);
    } catch (error) {
      console.error('Error fetching pattern performance:', error);
    }
  };

  const fetchFlaggedPatterns = async () => {
    try {
      const response = await fetch(`${API_BASE}/pattern-learning/flagged-patterns`);
      const data = await response.json();
      setFlaggedPatterns(data.flagged_patterns || []);
    } catch (error) {
      console.error('Error fetching flagged patterns:', error);
    }
  };

  const fetchPatternSuggestions = async () => {
    try {
      const response = await fetch(`${API_BASE}/pattern-learning/pattern-suggestions`);
      const data = await response.json();
      setPatternSuggestions(data.suggestions || []);
    } catch (error) {
      console.error('Error fetching pattern suggestions:', error);
    }
  };

  const loadPatternLearningData = () => {
    fetchLearningStats();
    fetchPatternPerformance();
    fetchFlaggedPatterns();
    fetchPatternSuggestions();
  };

  const renderPatternLearningDashboard = () => (
    <div className="pattern-learning-dashboard">
      <div className="dashboard-header">
        <h2>Pattern Learning Dashboard</h2>
        <button onClick={loadPatternLearningData} className="refresh-btn">
          ðŸ”„ Refresh Data
        </button>
      </div>

      {/* Learning Stats Overview */}
      {learningStats && (
        <div className="stats-overview">
          <h3>ðŸ“Š Learning Statistics</h3>
          <div className="stats-grid">
            <div className="stat-card">
              <div className="stat-value">{learningStats.total_patterns}</div>
              <div className="stat-label">Total Patterns</div>
            </div>
            <div className="stat-card">
              <div className="stat-value">{learningStats.flagged_patterns}</div>
              <div className="stat-label">Flagged Patterns</div>
            </div>
            <div className="stat-card">
              <div className="stat-value">{learningStats.total_feedback}</div>
              <div className="stat-label">Total Feedback</div>
            </div>
            <div className="stat-card">
              <div className="stat-value">{learningStats.feedback_accuracy.toFixed(1)}%</div>
              <div className="stat-label">Feedback Accuracy</div>
            </div>
            <div className="stat-card">
              <div className="stat-value">{learningStats.avg_success_rate}%</div>
              <div className="stat-label">Avg Success Rate</div>
            </div>
            <div className="stat-card">
              <div className="stat-value">{learningStats.total_extractions}</div>
              <div className="stat-label">Total Extractions</div>
            </div>
          </div>
        </div>
      )}

      {/* Flagged Patterns */}
      {flaggedPatterns.length > 0 && (
        <div className="flagged-patterns">
          <h3>ðŸš¨ Flagged Patterns ({flaggedPatterns.length})</h3>
          <div className="flagged-patterns-list">
            {flaggedPatterns.map((pattern, index) => (
              <div key={index} className="flagged-pattern-card">
                <div className="pattern-header">
                  <span className="pattern-id">{pattern.pattern_id}</span>
                  <span className="failure-count">âŒ {pattern.failure_count} failures</span>
                </div>
                <div className="pattern-stats">
                  <span>Success: {pattern.success_count}</span>
                  <span>Total: {pattern.total_extractions}</span>
                  <span>Confidence: {(pattern.average_confidence * 100).toFixed(1)}%</span>
                </div>
                <div className="pattern-text">
                  <strong>Pattern:</strong> <code>{pattern.pattern_text}</code>
                </div>
                <div className="pattern-field">
                  <strong>Field:</strong> {pattern.field_name}
                </div>
              </div>
            ))}
          </div>
        </div>
      )}

      {/* Pattern Suggestions */}
      {patternSuggestions.length > 0 && (
        <div className="pattern-suggestions">
          <h3>ðŸ’¡ Pattern Suggestions ({patternSuggestions.length})</h3>
          <div className="suggestions-list">
            {patternSuggestions.map((suggestion, index) => (
              <div key={index} className="suggestion-card">
                <div className="suggestion-header">
                  <span className="suggestion-type">{suggestion.suggestion_type}</span>
                  <span className="suggestion-confidence">
                    Confidence: {(suggestion.confidence * 100).toFixed(1)}%
                  </span>
                </div>
                <div className="suggestion-pattern">
                  <strong>Suggested Pattern:</strong>
                  <code>{suggestion.suggested_pattern}</code>
                </div>
                <div className="suggestion-reasoning">
                  <strong>Reasoning:</strong> {suggestion.reasoning}
                </div>
                <div className="suggestion-actions">
                  <button className="btn-primary">Apply Suggestion</button>
                  <button className="btn-secondary">Test Pattern</button>
                </div>
              </div>
            ))}
          </div>
        </div>
      )}

      {/* Pattern Performance Table */}
      {patternStats && patternStats.patterns && (
        <div className="pattern-performance">
          <h3>ðŸ“ˆ Pattern Performance</h3>
          <div className="performance-table">
            <table>
              <thead>
                <tr>
                  <th>Pattern ID</th>
                  <th>Field</th>
                  <th>Success Rate</th>
                  <th>Total</th>
                  <th>Confidence</th>
                  <th>Status</th>
                </tr>
              </thead>
              <tbody>
                {patternStats.patterns.map((pattern, index) => {
                  const successRate = pattern.total_extractions > 0 
                    ? (pattern.success_count / pattern.total_extractions * 100).toFixed(1)
                    : 0;
                  return (
                    <tr key={index} className={pattern.flagged_for_review ? 'flagged-row' : ''}>
                      <td>{pattern.pattern_id}</td>
                      <td>{pattern.field_name}</td>
                      <td>{successRate}%</td>
                      <td>{pattern.total_extractions}</td>
                      <td>{(pattern.average_confidence * 100).toFixed(1)}%</td>
                      <td>
                        {pattern.flagged_for_review ? (
                          <span className="status-flagged">ðŸš¨ Flagged</span>
                        ) : (
                          <span className="status-ok">âœ… OK</span>
                        )}
                      </td>
                    </tr>
                  );
                })}
              </tbody>
            </table>
          </div>
        </div>
      )}

      {!learningStats && (
        <div className="no-data">
          <p>No pattern learning data available. Click "Refresh Data" to load statistics.</p>
        </div>
      )}
    </div>
  );

  return (
    <div className="pattern-learning-container">
      <div className="tabs">
        <button 
          className={activeTab === 'endpoint-tester' ? 'active' : ''} 
          onClick={() => setActiveTab('endpoint-tester')}
        >
          Endpoint Tester
        </button>
        <button 
          className={activeTab === 'pattern-dashboard' ? 'active' : ''} 
          onClick={() => setActiveTab('pattern-dashboard')}
        >
          Pattern Learning Dashboard
        </button>
      </div>

      {activeTab === 'endpoint-tester' && renderEndpointTester()}
      {activeTab === 'pattern-dashboard' && renderPatternLearningDashboard()}

      {/* Regex Tester Modal */}
      {showRegexTester && (
        <div className="modal-overlay">
          <div className="modal">
            <div className="modal-header">
              <h3>Test Regex Pattern</h3>
              <button onClick={() => setShowRegexTester(false)}>Ã—</button>
            </div>
            <div className="modal-body">
              <div className="form-group">
                <label>Regex Pattern:</label>
                <input
                  type="text"
                  value={regexTestPattern}
                  onChange={(e) => setRegexTestPattern(e.target.value)}
                  placeholder="Enter regex pattern..."
                />
              </div>
              <div className="form-group">
                <label>Test Text:</label>
                <textarea
                  value={regexTestText}
                  onChange={(e) => setRegexTestText(e.target.value)}
                  placeholder="Enter text to test against..."
                  rows="4"
                />
              </div>
              <button onClick={testRegexPattern} className="btn-primary">
                Test Pattern
              </button>
              {regexTestResults && (
                <div className="test-results">
                  <h4>Test Results:</h4>
                  <p>Matches found: {regexTestResults.match_count}</p>
                  {regexTestResults.matches.length > 0 && (
                    <div>
                      <strong>Matches:</strong>
                      <ul>
                        {regexTestResults.matches.map((match, index) => (
                          <li key={index}>{match}</li>
                        ))}
                      </ul>
                    </div>
                  )}
                </div>
              )}
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default PatternLearningFeedback;
